const $accordionTriggers=Array.from(document.querySelectorAll(".accordion__item-trigger"));$accordionTriggers.forEach(e=>e.addEventListener("click",()=>{e.parentElement.classList.toggle("active")}));const $widgets=Array.from(document.querySelectorAll(".widget"));$widgets.forEach(e=>{e.addEventListener("click",t=>{t.target.classList.contains("widget__hide-trigger")&&e.classList.toggle("hidden")})});const $projectCards=Array.from(document.querySelectorAll(".project-card"));$projectCards.forEach(e=>{e.addEventListener("click",(function(){$projectCards.forEach(e=>e.classList.remove("active")),this.classList.add("active");const e=document.getElementById("project-type-info");e.querySelector(".project-type-info__title").innerText=this.dataset.title,e.querySelector(".project-type-info__text").innerText=this.dataset.text}))});